import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [profilePic, setProfilePic] = useState(localStorage.getItem("profilePic") || "");
  const navigate = useNavigate();

  useEffect(() => {
    const storedPic = localStorage.getItem("profilePic");
    if (storedPic) setProfilePic(storedPic);
  }, []);

  return (
    <div className="navbar w-screen h-[90px] bg-[#F4F4F4] px-[50px] flex items-center justify-between">
      <div className="logo text-2xl">NotePad</div>
      <div className="right flex items-center gap-[20px]">
        {profilePic ? (
          <img onClick={() => navigate("/profile")} src={profilePic} alt="Profile" className="w-[40px] h-[40px] rounded-full" />
        ) : (
          <div onClick={() => navigate("/profile")} className="w-[40px] h-[40px] rounded-full bg-gray-400"></div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
