import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Avatar from 'react-avatar';

const Navbar = () => {
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [profilePic, setProfilePic] = useState(null); // New state to hold the profile picture

  function getUserDetails() {
    fetch("http://localhost:8000/getUserDetails", {
      mode: "cors",
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        userId: localStorage.getItem("userID")
      })
    }).then(resp => resp.json()).then(data => {
      if (data.success === false) {
        navigate("/login");
      }
      else {
        setUserData(data);
        setProfilePic(data.profilePic); // Assuming the profile picture is in 'profilePic'
      }
    });
  }

  useEffect(() => {
    getUserDetails();
  }, []);

  return (
    <>
      <div className="navbar w-screen h-[90px] bg-[#F4F4F4] px-[50px] flex items-center justify-between">
        <div className="logo text-2xl">NotePad</div>

        <div className="right flex items-center gap-[20px]">
          <button className="btnNormal" onClick={() => { navigate("/addNewNote") }}>Add Note</button>
          
          {/* Conditionally render profile image or Avatar */}
          {profilePic ? (
            <img
              src={profilePic} // Display the profile picture
              alt="Profile"
              className="w-[40px] h-[40px] rounded-[50%] cursor-pointer"
              onClick={() => { navigate("/profile") }}
            />
          ) : (
            <Avatar
              name={userData ? userData.username : ""}
              size="50"
              round="50%"
              style={{ cursor: "pointer" }}
              onClick={() => { navigate("/profile") }}
            />
          )}
        </div>
      </div>
    </>
  );
}

export default Navbar;
